mapper.select:
SELECT * FROM TB_MEMBER
WHERE MB_NAME = #{mb_name} 
AND MB_CARD = #{mb_card}

mapper.update:
UPDATE TB_MEMBER
SET MB_NAME = #{mb_name},
	MB_BIRTHDATE = #{mb_birthdate}, MB_GENDER = #{mb_gender}
WHERE MB_CARD = #{mb_card} AND GYM_NAME = #{gym_name}

mapper.delete:
DELETE FROM TB_MEMBER
WHERE GYM_NAME = #{gym_name}
AND MB_NAME = #{mb_name} 
AND MB_CARD = #{mb_card}

mapper.select:
SELECT GYM_NAME 
FROM TB_MEMBER
WHERE MB_CARD = #{mb_card}
